mapper.select:
select * from ( select fdid,cycle_name,to_char(start_date,'yyyy-MM-dd') start_date, to_char(end_date,'yyyy-MM-dd') end_date,to_char(create_time,'yyyy-MM-dd hh24:mi:ss') create_time, (select staff_name from staff where fdid = a.creator) temp_str1, (select company_name from company a where a.fdid = org_id) select_company_name, (case a.state when '2' then '已开启' when '1' then '已关闭' else '其他' end) temp_str3 ,rownum pageno from sub_cycle a where 1=1 and isdeleted='0'
and cycle_name like '%'||#{cycle_name}||'%'
and org_id in
#{item}
) cc where 1=1
and cc.pageno >= #{start_num} and cc.pageno< #{end_num}

mapper.update:
update sub_cycle set isdeleted='1' ,version_time=to_date(#{version_time},'yyyy-mm-dd hh24:mi:ss'),operator=#{staff_id} where 1=1
and fdid in
#{item}

update sub_cycle set state = #{temp_str2}

	where fdid in
	
		#{item}

update rpt_person set
person_name=#{person_name},phone=#{person_phone},gender=#{person_gender},first_time=to_date(#{first_time},'yyyy-MM-dd'),
state = #{person_state},nation=#{person_nation}  where card_id = #{card_id}

