where av.proc_inst_id_ = av2.proc_inst_id_ and av2.name_ ='card_id' and
av2.text_=#{card_id}
union all
select Translate(a.flow_id USING NCHAR_CS) temp_str1 from co_rpt_reconds a
where 1=1 and a.isdeleted='0' and a.card_id =#{card_id}

select count(distinct card_id) temp_int1
from rpt_reconds a,sub_cycle sc where sc.fdid = a.sub_cycle and
a.isdeleted='0' and sc.cycle_code = #{rpt_cycle}

