mapper.select:
 	 select rq.level_code func_level
from staff2roles sr,role2function2qrylevel rq 
where sr.role_id = rq.role_id and sr.staff_id = #{staff_id} and rq.function_id= #{func_id}

select * from ( select a.fdid select_company_id,a.company_name select_company_name, (select name from company_type where fdid = a.company_type_cd and isdeleted='0')temp_str1, (select b.company_name from company b where a.parent_company_id = b.fdid ) temp_str2,a.create_time ,rownum pageno from company a where isdeleted='0'
and a.company_name like '%'||#{select_company_name}||'%'
) cc where 1=1
and cc.pageno > #{start_num} and cc.pageno<= #{end_num}

select * from ( select rl.fdid ,fr.fdid func_id, rl.role_name,fr.function_name func_name,(select function_name from functions a where a.fdid = fr.parent_function_id ) temp_str1, (select st.item_name from role2function2qrylevel rq ,sys_item st where rq.role_id = rl.fdid and rq.function_id = fr.fdid and st.fdid = rq.level_code and st.itype='QRYLEVEL' ) func_level , (select rq.version_time from role2function2qrylevel rq where rq.role_id = rl.fdid and rq.function_id = fr.fdid ) temp_str2, (select sa.staff_name from role2function2qrylevel rq,staff sa where rq.role_id = rl.fdid and rq.function_id = fr.fdid and sa.fdid = rq.operator ) staff_name,rownum pageno from roles rl ,role2function rf ,functions fr where rl.fdid = rf.role_id and rf.function_id = fr.fdid and fr.func_level = '3' and rf.btn_cd = 'view'
and rl.fdid = #{role_id}
order by rl.fdid asc ) cc where 1=1
and cc.pageno > #{start_num} and cc.pageno<= #{end_num}

 	 select * from (
        select sf.fdid ,sf.staff_number,sf.staff_name,sf.phone staff_phone,sf.address select_company_address,rl.role_name,cp.company_name  select_company_name,rownum pageno
from staff sf ,company cp,staff2company sc,staff2roles sr,roles rl
where sf.fdid = sc.staff_id and sc.company_id = cp.fdid 
and sf.fdid = sr.staff_id and sr.role_id = rl.fdid   and sf.isdeleted = '0'
   		
  				and   rl.fdid   = #{role_id}   
  		 
  		
  				and   sf.staff_name  like  '%'||#{temp_str1}||'%'   
  		
  		 
  			and cp.fdid  in
  			  
  					#{item}  
 			   
  		 
 			order by sf.fdid asc 
  		)  cc where 1=1    		
 		 
  				  and cc.pageno > #{start_num} and cc.pageno<= #{end_num}
